package Practise_Jan_05_2022;

public class TableOf7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 7;
		
		for (int i = 1; i<=10; i=i+1) {
			System.out.println(num*i);
		}
	}

}


